sap.ui.define(["QuickStartApplication/controller/BaseController"], function (BaseController) {
	"use strict";
	return BaseController.extend("QuickStartApplication.controller.workflowDetail", {

    	navegarMenu: function () {
            //This code was generated by the layout editor.
            this.getRouter().navTo("View1", []);
    	},

    	onPressGoToMaster: function (oEvent) {
            //This code was generated by the layout editor.
            //sap.ui.core.UIComponent.getRouterFor(this).navTo("Master2", []);
           this.getSplitAppObj().to(this.createId("detailWork"));
    	},
    	onPressNavToDetail : function(oEvent) {
    		this.getSplitAppObj().to(this.createId("detailWork"));
    	},
    	getSplitAppObj : function() {
    		var result = this.byId("SplitWorkFlow");
    		if (!result) {
    			jQuery.sap.log.info("SplitApp object can't be found");
    		}
    		return result;
    	}	
		
	});
});